import { useState } from "react";
import { Upload, message, Image } from "antd";
import { PlusOutlined } from "@ant-design/icons";
import { RcFile, UploadChangeParam } from "antd/lib/upload";
import { UploadFile } from "antd/lib/upload/interface";

function getBase64(
  img: RcFile,
  callback: (result: string | ArrayBuffer | null) => void,
) {
  const reader = new FileReader();
  reader.addEventListener("load", () => callback(reader.result));
  reader.readAsDataURL(img);
}

type ImageUploaderState = {
  imageUrl?: string;
};

function ImageUploader(): JSX.Element {
  const [state, setState] = useState<ImageUploaderState>({
    imageUrl: undefined,
  });

  const handleChange = (info: UploadChangeParam<UploadFile>) => {
    const fileLength = info.fileList.length;
    if (fileLength === 0) return;
    const fileObj = info.fileList[fileLength - 1].originFileObj;
    if (fileObj !== undefined) {
      const isLt2M = fileObj.size / 1024 / 1024 < 2;
      if (!isLt2M) {
        message.error("Image must smaller than 2MB!");
        return;
      }
      getBase64(fileObj, (imageUrl) => {
        const imageURL = imageUrl as string;
        if (imageURL === undefined) {
          message.error("Fail to read the file");
        }
        setState({
          imageUrl: imageURL ?? undefined,
        });
      });
    } else {
      message.error("Fail to open the image here");
      setState({
        imageUrl: undefined,
      });
    }
  };

  const uploadButton = (
    <div>
      <PlusOutlined style={{ color: "black" }} />
      <div
        className="gx-mt-1"
        style={{ fontSize: "14px", color: "rgba(0, 0, 0, 0.45)" }}
      >
        Upload
      </div>
    </div>
  );
  return (
    <Upload
      name="avatar"
      accept=".jpg,.png"
      listType="picture-card"
      className="avatar-uploader"
      style={{ width: "100px" }}
      showUploadList={false}
      beforeUpload={() => true}
      onChange={handleChange}
    >
      {state.imageUrl ? (
        <Image
          preview={false}
          width={86}
          height={86}
          src={state.imageUrl}
          fallback="data:image/png;base64,"
        />
      ) : (
        uploadButton
      )}
    </Upload>
  );
}

export default ImageUploader;
