import { Card, Divider, Row, Typography, Image, Col } from "antd";
import React, { useEffect, useState } from "react";
import { CommissionPost } from "../../../../../../../common/commission/data/models/commission_post";
import useComPostDetailHandler from "../../../../../../../guest/commission_post/presentation/features/commission_post_detail/use_compost_detail_handler";
import useConsumerOrderDetailHandler from "../use_consumer_order_detail_handler";

type ComPostProps = {
  compost:CommissionPost
}
function ComPostDetail({compost}:ComPostProps) {
  const { isMobile } = useConsumerOrderDetailHandler();
  const [ellipsis, setEllipsis] = useState(false);
  useEffect(() => {
    if (compost.title.length >= 25 && isMobile === true) setEllipsis(true);
  }, [isMobile]);
  return (
    <Card
      className="comic-shadow sm:shrink-0"
      style={{
        minHeight: "360px",
        padding: "0",
      }}
    >
      <Typography className="text-center text-lg sm:text-lg lg:text-lg font-bold">Detail Produk</Typography>
      <Divider className="my-2" />
      <Row justify="space-between">
        <Col className="text-base leading-10">
          <Col>
            <Typography.Text className="text-sm  sm:text-sm lg:text-lg pr-5">Judul Commission</Typography.Text>
          </Col>
          <Col>
            <Typography.Text className="text-lg ">Harga</Typography.Text>
          </Col>
        </Col>
        <Col className="text-base leading-10">
          <Col>
            <Typography.Text className="text-sm  sm:text-sm lg:text-lg font-bold">{ellipsis ? `${compost.title?.substring(0, 15)}... ` : compost.title}</Typography.Text>
          </Col>
          <Col>
            <Typography.Text className="text-lg font-bold text-right"> Rp.{compost.price}</Typography.Text>
          </Col>
        </Col>
      </Row>
      <div className="text-center">
        <Image
          src={compost.image_1}
          preview={false}
          loading="eager"
          className="max-h-44 object-contain"
          fallback="data:image/png;base64,"
        />
      </div>
    </Card>
  );
}

export default ComPostDetail;
