import { Card, Divider, Rate, Row, Typography, Image, Col, Tag } from "antd";
import React from "react";
import AssetConstants from "../../../../../../../../core/constants/asset_constants";
import { UtilMethods } from "../../../../../../../../core/utils/util_methods";
